import './Layout.css';
import Input from './Input';
import SingleTask from './singleTask';
import {useState} from 'react';

function Layout() {
  const [txts, setTxts] = useState([]);
  const [txt, setTxt] = useState('');
  function handleSubmit (e) {
    e.preventDefault()
    if (txt === "") {
      
      addTask(txt)
    }else{
      alert
    }
    setTxt("")
  }
  function addTask() {
    const newtexts = [...txts, txt]
    setTxts(newtexts)
  }

  return (
    <div id="layout">
      <header>To-Do List</header>
      <div id="input">
      <form id="input-text" >
        <input type="text" name="text" onChange={(e)=>{setTxt(e.target.value)}} value={txt} />
        <a onClick={handleSubmit} id="plus-icon">
          <i className="fa-solid fa-square-plus"></i>
        </a>
      </form>

      <div id="input-type">
          <div id="selected-item">
            <span>All</span>  
            <div className="toggle-down">
              <i className="fa-solid fa-caret-down"></i>
            </div>
          </div>
          <ul id="list-item">
            <li className='item'>Item 1</li>
            <li className='item'>Item 2</li>
            <li className='item'>Item 3</li>
            <li className='item'>Item 4</li>
          </ul>
      </div>
    </div>
      {txts.map((t, index) => <div key={index}><SingleTask name={t}/></div>)}
    </div>
  );
}

export default Layout;
